Create database HealthView1;
use HealthView1;
 

Create table usuario(id int auto_increment primary key not null,
username varchar(20) not null,
pass varchar(20) not null,
nombre varchar(30) not null,
aPaterno varchar(30) not null,
aMaterno varchar(30) not null,
sexo varchar(11) not null,
fechaNac date not null);


create table datosUsuario(id int(4) auto_increment primary key not null,
altura varchar(10) not null,
pulsoRadial varchar(10) not null,
peso varchar(10) not null,
actFis varchar(30) not null,
nombreUs int not null,
foreign key(nombreUs) references usuario(id));

	create table alimentos(id int auto_increment primary key not null,
	nombreAl varchar(100) not null,
	porcion varchar (100) not null,
	cKal varchar(10) not null,
	grasa varchar(10) not null,
	carbohidratos varchar(10) not null,
	proteina varchar(10) not null,
	imagen varchar(10));




/*Back end, tabla de historial*/

create table historial(id bigint auto_increment primary key not null,
alturahist varchar(10) not null,
pulsoRadial varchar(10) not null,
pesohist varchar(20) not null,
actFis varchar(10) not null,
userfk int not null,
fechamod date not null,
foreign key (userfk) references usuario(id) );

/*Back end, tabla historial Alimentaci√≥n*/

create table historialAlimentacion(id int auto_increment primary key not null,
